    parent::__construct($clientOrConfig);
    $this->rootUrl = $rootUrl ?: 'https://runtimeconfig.googleapis.com/';
    $this->rootUrlTemplate = $rootUrl ?: 'https://runtimeconfig.UNIVERSE_DOMAIN/';
    $this->servicePath = '';
    $this->batchPath = 'batch';
    $this->version = 'v1';
